import React, {useState, useEffect} from 'react'
import { FaBars } from 'react-icons/fa'
import {IconContext} from 'react-icons/lib'
import img from '../../Images/EloElevation-2.png'
import {
     Nav, 
     NavbarContainer, 
     NavLogo, 
     MobileIcons, 
     NavMenu, 
     NavItem, 
     NavLinks, 
     NavBtn, 
     NavBtnLink, 
     NavBtnLink2
    } from './NavbarElements'
import {animateScroll as scroll} from 'react-scroll'

const Navbar = ({ toggle }) => {
    const [scrollNav, setScrollNav] = useState(false)

    const changeNav = () => {
        if(window.scrollY >= 80) {
            setScrollNav(true)
        } else {
            setScrollNav(false)
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', changeNav)
    }, []);

    const toggleHome = () => {
        scroll.scrollToTop();
    }

    return (
        <>
            <IconContext.Provider value={{color: '#fff'}}>
                <Nav scrollNav={scrollNav}>
                    <NavbarContainer>
                        <NavLogo onClick={toggleHome} to='/'>
                            Elo Elevation
                        </NavLogo>
                        <MobileIcons onClick={toggle}>
                            <FaBars />
                        </MobileIcons>
                        <NavMenu>
                            <NavItem>
                                <NavLinks 
                                to='about' 
                                smooth={true}
                                duration={500}
                                spy={true}
                                exact='true'
                                offset={-80}
                                >
                                    About
                                </NavLinks>
                            </NavItem>
                            <NavItem>
                                <NavLinks 
                                to='discover'
                                smooth={true}
                                duration={500}
                                spy={true}
                                exact='true'
                                offset={-80}>
                                    Discover
                                </NavLinks>
                            </NavItem>
                            <NavItem>
                                <NavLinks 
                                to='services'
                                smooth={true}
                                duration={500}
                                spy={true}
                                exact='true'
                                offset={-80}>
                                    Services
                                </NavLinks>
                            </NavItem>
                        </NavMenu>
                        <NavBtn>
                            <NavBtnLink2 to="/login">
                                Login
                            </NavBtnLink2>
                            <NavBtnLink to="/signup">
                                Sign Up
                            </NavBtnLink>
                        </NavBtn>
                    </NavbarContainer>
                </Nav>
            </IconContext.Provider>
        </>
    );
};

export default Navbar
