import styled from 'styled-components'
import {FaFork} from 'react-icons/fa'


export const TileWrapper = styled.li`
    border: 1px solid orange;
    list-style-type: none;
    background-color: white;
    border-radius: 20px;
    box-shadow: 1px 5px 8px 2px rgba(0,0,0,0.15);

`
export const TileIconWrap = styled.div`
    max-width: 100px;
`

export const TileIcon = styled(FaFork) `
color: #fff;
`



export const TileHeadline = styled.h1`
    margin-bottom: 24px;
    font-size: 48px;
    line-height: 1.1;
    font-weight: 600;
    color: #394149;
`
