import { FaBook } from 'react-icons/fa'
import styled from 'styled-components'



export const TileWrapper = styled.li`
    border: 1px solid orange;
    list-style-type: none;
    background-color: white;
    border-radius: 10px;
    box-shadow: rgb(86 93 100 / 10%) 0px 0.125rem 1.25rem 0px;

    &:hover {
        box-shadow: 0.2s ease-in-out;
    }
`

export const TileHeader = styled.div`
    display: grid;
    grid-template-columns: 100px 1fr;
    align-items: center;
    grid-template-rows: 70% 30%;
`

export const TileIconWrap = styled.div`
    background: transparent;
    margin-top: 10px;
    margin-left: 15px;
    grid-row: 1 / span 2;
    align-items: center;


`

export const TileIcon = styled(FaBook) `
    color: #000;
    width: 60px;
    height: 60px;
`



export const TileHeadline = styled.h1`
    font-size: 48px;
    line-height: 1.1;
    font-weight: 600;
    color: #394149;

`

export const TileSubheadline = styled.p`
    font-size: 21px;
    font-weight: 400px;
`
