import {MainDiv,  
    AnnouncementContainer, 
    CourseContent, 
    AnnouncementTitleH1, 
    AnnouncementWrapper, 
    AnnouncementP,
    CoursesWrapper,
    ModuleWrapper,
    ModuleGrid
} from "./AnnouncementsElements"
import firebaseConfig from "../../../config";
import React, {useContext} from 'react'
import { Redirect } from "react-router-dom";
import { AuthContext } from "../../Auth";
import CourseList from '../Courses Body/Index'
import {Module1} from "../Courses Body/Data"

const MainPage = () => {

    const { currentUser } = useContext(AuthContext);
  if (!currentUser) {
    return <Redirect to="/login" />; 
  }


    return (

            <CourseContent>
                <AnnouncementWrapper>
                <AnnouncementContainer>  
                        <AnnouncementTitleH1> 
                            Welcome to Elo Elevation
                        </AnnouncementTitleH1>
                        <AnnouncementP>
                            This is the dashboard, if you can see this you're logged in.
                        </AnnouncementP>
                        <button onClick={() => firebaseConfig.auth().signOut()}>Sign out</button>
                </AnnouncementContainer>
                </AnnouncementWrapper>
                <CourseList {...Module1}>
                </CourseList>
            </CourseContent>

    )
}

export default MainPage
